import React, { Component } from 'react'
import axios from 'axios'
import { useParams } from "react-router-dom";


function withParams(Component) {
    return props => <Component {...props} params={useParams()} />;
  }




class CreateChar extends Component {

    constructor(props){
        super(props);

        this.state = {

        _id: '',
        nombre: "",
        clase: "Guerrero",
        raza: "Humano",


        fuerza: "",
        destreza: "",
        constitucion: "",
        inteligencia: "",
        sabiduria: "",
        carisma: "",
        val: 0,
            valores: [
                Math.floor(Math.random() * (18 - 1 + 1) + 1),
                Math.floor(Math.random() * (18 - 1 + 1) + 1),
                Math.floor(Math.random() * (18 - 1 + 1) + 1),
                Math.floor(Math.random() * (18 - 1 + 1) + 1),
                Math.floor(Math.random() * (18 - 1 + 1) + 1),   
                Math.floor(Math.random() * (18 - 1 + 1) + 1)
            ],

        };


    }






    async componentDidMount(){
        const res = await axios.get('http://localhost:4000/chars')

        this.setState({



        })

        if(this.props.params.id){
            const res = await axios.get('http://localhost:4000/chars' + this.props.params.id);
            this.setState({
               _id: this.props.params.id,

               nombre: res.data.nombre,
               clase: res.data.clase,
               raza: res.data.raza,

            fuerza: res.data.fuerza,
            destreza: res.data.destreza,
            constitucion: res.data.constitucion,
            inteligencia: res.data.inteligencia,
            sabiduria: res.data.sabiduria,
            carisma: res.data.carisma


            })
        }
    }



    onSubmit = async (e) => {
        e.preventDefault();
        const newChar ={


            nombre: this.state.nombre,
            clase: this.state.clase,
            raza: this.state.raza,

            fuerza: this.state.valores[0],
            destreza: this.state.valores[1],
            constitucion: this.state.valores[2],
            inteligencia: this.state.valores[3],
            sabiduria: this.state.valores[4],
            carisma: this.state.valores[5]


        }
        if(this.state.editing){
            await axios.put('http://localhost:4000/chars' + this.state._id, newChar)
        }else{
            await axios.post('http://localhost:4000/chars', newChar);
        }

        window.location.href = '/';
    }
    onInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value,

        })
    }
    onChangeDate = date => {
        this.setState({date})
    }

    render(){


        return(
            <div className="col-md-6 offset-md-3">
                 <div className="card card-body">
                    <h4>Aqui inicia tu destino</h4>
                    <div className="form-group">

                    {/* Nombre */}
                    <label>Nombre: </label>
                    <input 
                            type="text" 
                            className="form-control" 
                            placeholder="Inserta tu nombre, lord" 
                            name="nombre"
                            onChange={this.onInputChange}
                            value={this.state.nombre}
                            required/> 

                    {/* Clase */}
                    <label>Clase: </label>
                    <select
                    className="form-control"
                    name="clase"
                    onChange={this.onInputChange}
                    value={this.state.clase}
                    >
                        <option defaultChecked>Guerrero</option>
                        <option>Mago</option>
                        <option>Bribon</option>



                    </select>
                    <br></br>
                     {/* Raza */}
                     <label>Raza: </label>
                    <select
                    className="form-control"
                    name="raza"
                    onChange={this.onInputChange}
                    value={this.state.raza}                   
                    >
                        <option defaultChecked>Humano</option>
                        <option>Elfo</option>
                        <option>Enano</option>
                        <option>Mediano</option>
                    </select>
                </div>

            <div className="container">
                <div className="row">
                        <div className="col-sm">
                        Fuerza
                        <input  onChange={this.onInputChange} value={this.state.valores[0]} type="number" id="quantity" name="fuerza" min="1" max="5"></input>
                        </div>
                        <div className="col-sm">
                        Destreza
                        <input  onChange={this.onInputChange} value={this.state.valores[1]} type="number" id="quantity" name="destreza" min="1" max="5"></input>
                        </div>
                        <div className="col-sm">
                        Constitucion
                        <input  onChange={this.onInputChange} value={this.state.valores[2]} type="number" id="quantity" name="constitucion" min="1" max="5"></input>
                        </div>
                </div>
            </div>

            <div className="container">
                <div className="row">
                        <div className="col-sm">
                        Inteligencia
                        <input  onChange={this.onInputChange} value={this.state.valores[3]} type="number" id="quantity" name="inteligencia" min="1" max="5"></input>
                        </div>
                        <div className="col-sm">
                        Sabiduria
                        <input  onChange={this.onInputChange} value={this.state.valores[4]} type="number" id="quantity" name="sabiduria" min="1" max="5"></input>
                        </div>
                        <div className="col-sm" >
                        Carisma
                        <input  onChange={this.onInputChange} value={this.state.valores[5]} type="number" id="quantity" name="carisma" min="1" max="5"></input>
                        </div>
                </div>
            </div>

                <form onSubmit={this.onSubmit}>
                        <button type="submit" className="btn btn-primary">
                            ¿Seras capaz de unirlos? 
                        </button>
                    </form>

                          {/* ejemplo de funcion */}
                        <button className="btn btn-primary" 
                        onClick={() => this.setState({val: this.state.val + 1})}>
                            {this.state.val}
                        </button>

                        <button className="btn btn-primary" 
                        onClick={() => this.setState({this: Math.floor(Math.random() * (18 - 1 + 1) + 1)})}>
                            No me gusta mi destino! {this.state.fuerza}
                        </button>
            </div>
        </div>
        )
    }
}


export default withParams(CreateChar);
