import React, { Component } from 'react'
import axios from 'axios'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import { useParams } from "react-router-dom";


function withParams(Component) {
    return props => <Component {...props} params={useParams()} />;
  }


class CreateNote extends Component {
    state={
        users: [],
        userSelected: '',
        title: '',
        content:'',
        date: new Date(),
        editing: false,
        _id: ''
    }
    async componentDidMount(){
        const res = await axios.get('http://localhost:4000/chars')
        this.setState({
           users: res.data,
           userSelected: res.data[0].username
        })

        if(this.props.params.id){
            const res = await axios.get('http://localhost:4000/api/notes/' + this.props.params.id);
            this.setState({
                title: res.data.title,
                content: res.data.content,
                date: new Date(res.data.date),
                userSelected: res.data.author,
                editing: true,
               _id: this.props.params.id
            })
        }
    }

    onSubmit = async (e) => {
        e.preventDefault();
        const newNote ={
            title: this.state.title,
            content: this.state.content,
            date: this.state.date,
            author: this.state.userSelected
        }
        if(this.state.editing){
            await axios.put('http://localhost:4000/chars' + this.state._id, newChar)
        }else{
            await axios.post('http://localhost:4000/chars', newChar);
        }

        window.location.href = '/';
    }
    onInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    onChangeDate = date => {
        this.setState({date})
    }

    render(){
        return(
            <div className="col-md-6 offset-md-3">
                 <div className="card card-body">
                    <h4>Start your fate!</h4>
                 </div>
            </div>
        )
    }

}
export default withParams(CreateNote);
