import React, { Component } from 'react'
import {useState} from 'react'
import axios from 'axios'
import {format} from "timeago.js"
import {Link} from 'react-router-dom'


export default class CharList extends Component{
    state = {
        chars: [],
    }

    async componentDidMount(){
        this.getChars()
    }

    async getChars(){
        const res = await axios.get("http://localhost:4000/chars")
        this.setState({chars: res.data})
    }

    render(){
        return(
            <div className="row"> 
            {
            this.state.chars.map(char => (
                <div className="col-md-4 p-2" key={char._id}>
                    <div className='card'>
                        <div className="card-header d-flex justify-content-between"> 
                            <h5>{char.nombre}</h5>
                        </div>
                        <div className="card-body">
                            <p></p>
                                <p>{char.clase}</p>
                                <p>{char.raza}</p>

                                <p>{char.fuerza}</p>
                                <p>{char.destreza}</p>
                                <p>{char.constitucion}</p>

                                <p>{char.inteligencia}</p>
                                <p>{char.sabiduria}</p>
                                <p>{char.carisa}</p>

                                <p>{char.frase}</p>
                            </div>
                    </div>
                </div>
            ))
    }
    </div>
        )
    }
}
