import React, { Component } from 'react'
import axios from 'axios'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import { useParams } from "react-router-dom";


function withParams(Component) {
    return props => <Component {...props} params={useParams()} />;
  }


class CreateChar extends Component {
    state={
        _id: '',
        nombre: "",
        claseSeleccionada: "",
        razaSeleccionada: "",
    }
    async componentDidMount(){
        const res = await axios.get('http://localhost:4000/chars')
        this.setState({



        })

        if(this.props.params.id){
            const res = await axios.get('http://localhost:4000/chars' + this.props.params.id);
            this.setState({
               _id: this.props.params.id,

               nombre: res.data.nombre,
               clase: res.data.claseSeleccionada,
               raza: res.data.claseSeleccionada


            })
        }
    }

    onSubmit = async (e) => {
        e.preventDefault();
        const newChar ={


            nombre: this.state.nombre,
            clase: this.state.clase,
            raza: this.state.raza 
        }
        if(this.state.editing){
            await axios.put('http://localhost:4000/chars' + this.state._id, newChar)
        }else{
            await axios.post('http://localhost:4000/chars', newChar);
        }

        window.location.href = '/';
    }
    onInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    onChangeDate = date => {
        this.setState({date})
    }

    render(){
        return(
            <div className="col-md-6 offset-md-3">
                 <div className="card card-body">
                    <h4>Aqui inicia tu destino</h4>
                    <div className="form-group">

                    {/* Nombre */}
                    <label>Nombre: </label>
                    <input 
                            type="text" 
                            className="form-control" 
                            placeholder="Inserta tu nombre, lord" 
                            name="nombre    "
                            onChange={this.onInputChange}
                            value={this.state.nombre}
                            required/> 

                    {/* Clase */}
                    <label>Clase: </label>
                    <select
                    className="form-control"
                    name="clase"
                    onChange={this.onInputChange}
                    value={this.state.clase}
                    >
                        <option>Guerrero</option>
                        <option>Mago</option>
                        <option>Bribon</option>



                    </select>
                    <br></br>
                     {/* Raza */}
                     <label>Raza: </label>
                    <select
                    className="form-control"
                    name="raza"
                    onChange={this.onInputChange}
                    value={this.state.raza}                   
                    >
                        <option>Humano</option>
                        <option>Elfo</option>
                        <option>Enano</option>
                        <option>Mediano</option>
                    </select>

                </div>

            <div class="container">
                <div class="row">
                        <div class="col-sm">
                        Vida
                        </div>
                        <div class="col-sm">
                        Ataque
                        </div>
                        <div class="col-sm">
                        Pepega
                        </div>
                </div>
            </div>

                <form onSubmit={this.onSubmit}>
                        <button type="submit" className="btn btn-primary">
                            Guardar
                        </button>
                    </form>

            </div>
        </div>
        )
    }

}
export default withParams(CreateChar);
