import React, { Component } from 'react'
import { Link } from 'react-router-dom'    
import axios from 'axios'
import { useParams } from "react-router-dom";


function withParams(Component) {
    return props => <Component {...props} params={useParams()} />;
  }
class Example extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      count: 0
    };
  }

  async componentDidMount(){
    const res = await axios.get('http://localhost:4000/chars')

    document.title = `You clicked ${this.state.val} times`;

    this.setState({

    })

    if(this.props.params.id){
        const res = await axios.get('http://localhost:4000/chars' + this.props.params.id);
        this.setState({
           _id: this.props.params.id,

           nombre: res.data.nombre,
           clase: res.data.clase,
           raza: res.data.raza,

        fuerza: res.data.fuerza,
        destreza: res.data.destreza,
        constitucion: res.data.constitucion,
        inteligencia: res.data.inteligencia,
        sabiduria: res.data.sabiduria,
        carisma: res.data.carisma


        })
    }
}



  render() {
    return (
      <div>
        <p>You clicked {this.state.count} times</p>
        <button onClick={() => this.setState({ count: this.state.count + 1 })}>
          Click me
        </button>
      </div>
    );
  }
}
export default withParams(Example);
